int main() {
    int grade;
    char letter;
    
    printf("Enter your grade (0-100): ");
    scanf("%d", &amp;grade);
    
    // Decision making magic
    if (grade &gt;= 90) {
        letter = 'A';
    } else if (grade &gt;= 80) {
        letter = 'B';
    } else if (grade &gt;= 70) {
        letter = 'C';
    } else if (grade &gt;= 60) {
        letter = 'D';
    } else {
        letter = 'F';
    }
    
    // Switch statement for feedback
    switch (letter) {
        case 'A':
            printf("Excellent work!\n");
            break;
        case 'B':
            printf("Good job!\n");
            break;
        case 'C':
            printf("Average performance.\n");
            break;
        case 'D':
            printf("Below average.\n");
            break;
        default:
            printf("Need improvement.\n");
    }
    
    return 0;
}</code></pre>
