int main() {
    int i, sum = 0;
    int numbers[] = {10, 20, 30, 40, 50};
    
    // For loop magic - counting spell
    printf("Counting from 1 to 5:\n");
    for (i = 1; i &lt;= 5; i++) {
        printf("%d ", i);
    }
    printf("\n\n");
    
    // While loop magic - sum calculation
    i = 0;
    while (i &lt; 5) {
        sum += numbers[i];
        printf("Adding %d, sum = %d\n", numbers[i], sum);
        i++;
    }
    
    printf("\nTotal sum: %d\n", sum);
    
    // Pattern magic with nested loops
    printf("\nMagical pattern:\n");
    for (i = 1; i &lt;= 5; i++) {
        for (int j = 1; j &lt;= i; j++) {
            printf("* ");
        }
        printf("\n");
    }
    
    return 0;
}</code></pre>
